import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from './Header';
import ContentContainer from './ContentContainer';
import EndMessage from './EndMessage';
import MenuModal from './MenuModal';
import Settings from './Settings';
import styles from '../styles/QuizPage.module.css';
import { fetchQuizData, markBlockAsUsed } from '../store/actions';

const QuizPage = ({ showMainMenu, handleNewGame }) => {
  const dispatch = useDispatch();
  const selectedMode = useSelector((state) => state.quiz.selectedMode); // Retrieve selectedMode directly from Redux
  const quizState = useSelector((state) => state.quiz[selectedMode]);
  const [isSettingsVisible, setIsSettingsVisible] = useState(false);

  useEffect(() => {
    if (selectedMode) {
      console.log('Fetching quiz data for mode:', selectedMode);
      dispatch(fetchQuizData(selectedMode));
    }
  }, [selectedMode, dispatch]);

  const showSettings = () => {
    setIsSettingsVisible(true);
  };

  const hideSettings = () => {
    setIsSettingsVisible(false);
  };

  const markBlock = (categoryName, blockId) => {
    dispatch(markBlockAsUsed({ mode: selectedMode, categoryName, blockId }));
  };

  if (!quizState) {
    return <div>Данные не загружены</div>;
  }

  return (
    <div className={styles.quiz_page}>
      <Header />
      <ContentContainer
        usedBlocks={quizState.usedBlocks || {}}
        markBlockAsUsed={markBlock}
        data={quizState.categories}
        mode={selectedMode}
      />
      <EndMessage />
      <MenuModal showSettings={showSettings} showMainMenu={showMainMenu} />
      {isSettingsVisible && <Settings onClose={hideSettings} />}
    </div>
  );
};

export default QuizPage;
